import React from "react";
import { useLocation } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./SearchResults.css"; // style if needed

const mockData = [
  {
    operator: "Akota Transport",
    from: "Rajshahi",
    to: "Dhaka",
    departure: "05:30 AM",
    arrival: "11:00 AM",
    fare: "৳690",
    seats: 40,
  },
  {
    operator: "Hanif Enterprise",
    from: "Rajshahi",
    to: "Dhaka",
    departure: "06:00 AM",
    arrival: "01:30 PM",
    fare: "৳690",
    seats: 41,
  },
];

const SearchResults = () => {
  const { state } = useLocation();
  const { from, to, date } = state || {};

  return (
    <div className="container mt-4">
      <h4>
        Showing results for: <strong>{from}</strong> ➡ <strong>{to}</strong> on{" "}
        <strong>{date}</strong>
      </h4>
      <div className="mt-4">
        {mockData.map((bus, idx) => (
          <div key={idx} className="card mb-3">
            <div className="card-body d-flex justify-content-between align-items-center">
              <div>
                <h5>{bus.operator}</h5>
                <p>
                  {bus.departure} → {bus.arrival}
                </p>
              </div>
              <div>
                <p>{bus.fare}</p>
                <p>{bus.seats} seats</p>
                <button className="btn btn-success">Book Ticket</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchResults;
