import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import "./SeatSelection.css";

const seatLabels = [
  "A1",
  "A2",
  "A3",
  "A4",
  "B1",
  "B2",
  "B3",
  "B4",
  "C1",
  "C2",
  "C3",
  "C4",
  "D1",
  "D2",
  "D3",
  "D4",
  "E1",
  "E2",
  "E3",
  "E4",
  "F1",
  "F2",
  "F3",
  "F4",
  "G1",
  "G2",
  "G3",
  "G4",
];

function SeatSelection() {
  const { state } = useLocation();
  const { bus } = state || {};
  const [selectedSeats, setSelectedSeats] = useState([]);

  const toggleSeat = (seat) => {
    if (selectedSeats.includes(seat)) {
      setSelectedSeats(selectedSeats.filter((s) => s !== seat));
    } else if (selectedSeats.length < 4) {
      setSelectedSeats([...selectedSeats, seat]);
    }
  };

  return (
    <div className="seat-page container mt-4">
      <h4 className="text-success mb-3">
        {bus?.company || "Bus"} Seat Selection
      </h4>
      <div className="d-flex justify-content-between mb-3">
        <div>
          <strong>From:</strong> {bus.departCity}
          <br />
          <strong>To:</strong> {bus.arrivalCity}
        </div>
        <div>
          <strong>Departure:</strong> {bus.departTime}
          <br />
          <strong>Arrival:</strong> {bus.arrivalTime}
        </div>
      </div>
      <p className="text-muted">Maximum 4 seats can be selected.</p>

      <div className="seats-container mb-3">
        {seatLabels.map((seat) => (
          <button
            key={seat}
            className={`seat ${selectedSeats.includes(seat) ? "selected" : ""}`}
            onClick={() => toggleSeat(seat)}
          >
            {seat}
          </button>
        ))}
      </div>

      <div className="text-center mt-4">
        <p>{selectedSeats.length} ticket(s) selected</p>
        <button
          className="btn btn-success w-50"
          disabled={selectedSeats.length === 0}
        >
          Continue
        </button>
      </div>
    </div>
  );
}

export default SeatSelection;
