import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./SeatSelection.css";

const seatLayout = [
  ["A1", "A2", "A3", "A4"],
  ["B1", "B2", "B3", "B4"],
  ["C1", "C2", "C3", "C4"],
  ["D1", "D2", "D3", "D4"],
  ["E1", "E2", "E3", "E4"],
  ["F1", "F2", "F3", "F4"],
  ["G1", "G2", "G3", "G4"],
];

function SeatSelection() {
  const location = useLocation();
  const navigate = useNavigate();
  const { bus } = location.state || {};

  const [selectedSeats, setSelectedSeats] = useState([]);
  const [bookedSeats, setBookedSeats] = useState([]);

  useEffect(() => {
    // Prevent unauthorized access
    const isLoggedIn = localStorage.getItem("user");
    if (!isLoggedIn) {
      alert("You must be logged in to book tickets.");
      navigate("/login", {
        state: { from: { pathname: "/seats", bus } },
      });
    }

    // Load already booked seats from localStorage
    const stored =
      JSON.parse(localStorage.getItem(`bookedSeats_${bus?.id}`)) || [];
    setBookedSeats(stored);
  }, [bus, navigate]);

  const toggleSeat = (seat) => {
    if (bookedSeats.includes(seat)) return;

    if (selectedSeats.includes(seat)) {
      setSelectedSeats(selectedSeats.filter((s) => s !== seat));
    } else {
      if (selectedSeats.length >= 4) {
        alert("You can select a maximum of 4 seats.");
        return;
      }
      setSelectedSeats([...selectedSeats, seat]);
    }
  };

  const handleBooking = () => {
    const updated = [...bookedSeats, ...selectedSeats];
    localStorage.setItem(`bookedSeats_${bus.id}`, JSON.stringify(updated));
    alert("Booking successful!");
    navigate("/");
  };

  if (!bus)
    return (
      <p className="text-center mt-5 text-danger">Invalid bus selection</p>
    );

  return (
    <div className="container my-4">
      <h4 className="text-success mb-3">{bus.company}</h4>
      <p>
        {bus.departCity} → {bus.arrivalCity} on {bus.departDate} | Coach No. #
        {bus.id}
      </p>
      <p className="fw-bold">Select up to 4 seats</p>

      <div className="seat-grid">
        {seatLayout.map((row, i) => (
          <div key={i} className="seat-row">
            {row.map((seat) => {
              const isBooked = bookedSeats.includes(seat);
              const isSelected = selectedSeats.includes(seat);
              return (
                <button
                  key={seat}
                  className={`seat ${
                    isBooked ? "booked" : isSelected ? "selected" : ""
                  }`}
                  onClick={() => toggleSeat(seat)}
                  disabled={isBooked}
                >
                  {seat}
                </button>
              );
            })}
          </div>
        ))}
      </div>

      <div className="mt-4">
        <p>
          {selectedSeats.length} ticket(s) selected –{" "}
          <strong>৳{selectedSeats.length * bus.price}</strong>
        </p>
        <button
          className="btn btn-success"
          onClick={handleBooking}
          disabled={selectedSeats.length === 0}
        >
          Confirm Booking
        </button>
      </div>
    </div>
  );
}

export default SeatSelection;
